class User {\n    private $name;\n    private $email;\n\n    public function __get($property) {\n        if (property_exists($this, $property)) {\n            return $this->$property;\n        } else {\n            throw new Exception("Property $property does not exist");\n        }\n    }\n\n    public function __set($property, $value) {\n        if (property_exists($this, $property)) {\n            $this->$property = $value;\n        } else {\n            throw new Exception("Property $property does not exist");\n        }\n    }\n}