require_once('lib/nusoap.php');\n$wsdl = "http://localhost:8080/HelloWorldWS/sayHiService?WSDL";\n$client = new nusoap_client($wsdl, 'wsdl');\n\n// Check if there was an error creating the client\nif ($client->getError()) {\n    echo "Error creating client: " . $client->getError();\n    exit;\n}\n\n$param = array('name' => 'John');\n$result = $client->call('sayHi', $param); // 'sayHi' might be the correct method name\n\n// Check if there was an error making the SOAP call\nif ($client->getError()) {\n    echo "Error making SOAP call: " . $client->getError();\n    exit;\n}\n\necho $result;\nunset($client);