<?php\n$accessKey = 'YOUR_ACCESS_KEY';\n$userId = 'YOUR_USER_ID';\n$password = 'YOUR_PASSWORD';\n\n$wsdlUrl = 'https://wwwcie.ups.com/webservices/Ship.wsdl';\n\n$client = new SoapClient($wsdlUrl, array(\n    'soap_version' => SOAP_1_1,\n    'trace' => 1\n));\n\n$shipRequest = array(\n    'Request' => array(\n        'TransactionReference' => array(\n            'CustomerContext' => 'YourCustomerContext'\n        )\n    ),\n    'Shipment' => array(\n        'Shipper' => array(\n            'Name' => 'YourCompany',\n            'Address' => array(\n                'AddressLine' => 'YourAddress',\n                'City' => 'YourCity',\n                'StateProvinceCode' => 'YourState',\n                'PostalCode' => 'YourZip',\n                'CountryCode' => 'US'\n            )\n        ),\n        'ShipTo' => array(\n            'Name' => 'RecipientName',\n            'Address' => array(\n                'AddressLine' => 'RecipientAddress',\n                'City' => 'RecipientCity',\n                'StateProvinceCode' => 'RecipientState',\n                'PostalCode' => 'RecipientZip',\n                'CountryCode' => 'US'\n            )\n        ),\n        'Service' => array(\n            'Code' => '03', // UPS Ground\n            'Description' => 'UPS Ground'\n        ),\n        'Package' => array(\n            'PackagingType' => array(\n                'Code' => '02', // UPS Package\n                'Description' => 'UPS Package'\n            ),\n            'Dimensions' => array(\n                'Length' => '10',\n                'Width' => '10',\n                'Height' => '10'\n            ),\n            'PackageWeight' => array(\n                'Weight' => '5'\n            )\n        ),\n        'LabelSpecification' => array(\n            'LabelImageFormat' => array(\n                'Code' => 'GIF'\n            )\n        )\n    )\n);\n\n$auth = array(\n    'UPSSecurity' => array(\n        'UsernameToken' => array(\n            'Username' => $userId,\n            'Password' => array(\n                'Value' => $password\n            )\n        ),\n        'ServiceAccessToken' => array(\n            'AccessLicenseNumber' => $accessKey\n        )\n    )\n);\n\n$ShipConfirmResponse = $client->__soapCall('ProcessShipment', array($shipRequest), NULL, $auth);\n\nif ($ShipConfirmResponse->ShipmentResults->ShipmentIdentificationNumber) {\n    echo "Shipment created: " . $ShipConfirmResponse->ShipmentResults->ShipmentIdentificationNumber;\n    // Save the shipping label to a file\n    file_put_contents('shipping_label.gif', $ShipConfirmResponse->ShipmentResults->LabelImage->GraphicImage);\n} else {\n    echo "Error creating shipment: " . $ShipConfirmResponse->Fault->faultstring;\n}