class A\n{\n    private $name;\n    private $age;\n\n    private $allowedProperties = ['name', 'age'];\n\n    public function __get($var)\n    {\n        if (in_array($var, $this->allowedProperties)) {\n            return $this->$var;\n        } else {\n            throw new InvalidArgumentException("Property '$var' does not exist");\n        }\n    }\n\n    public function __set($var, $value)\n    {\n        if (in_array($var, $this->allowedProperties)) {\n            $this->$var = $value;\n        } else {\n            throw new InvalidArgumentException("Property '$var' does not exist");\n        }\n    }\n}