try {\n    // Assuming you have a PDO connection\n    $pdo = new PDO('mysql:host=your_host;dbname=your_database', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->query('\n        SELECT\n            SUM(\n                CASE\n                    WHEN package = 3 THEN 5\n                    WHEN package = 4 THEN 10\n                    WHEN package = 1 THEN 1\n                    ELSE 0\n                END\n            ) AS total_value\n        FROM your_table_name;\n    ');\n\n    $result = $stmt->fetch();\n    if ($result) {\n        echo $result['total_value'];\n    } else {\n        echo 'No result found.';\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}