function convertDMS_To_DD($dms) {\n    list($degrees, $minutes, $seconds, $direction) = explode(' ', $dms);\n    $decimal_degrees = $degrees + ($minutes / 60) + ($seconds / 3600);\n\n    if ($direction == 'S' || $direction == 'W') {\n        $decimal_degrees = -$decimal_degrees;\n    }\n\n    return $decimal_degrees;\n}\n\n// Example usage:\n$dms_latitude = "37 14 6 N";\n$dms_longitude = "115 48 40 W";\n\n$dd_latitude = convertDMS_To_DD($dms_latitude);\n$dd_longitude = convertDMS_To_DD($dms_longitude);\n\necho "Decimal Degrees: $dd_latitude, $dd_longitude";