$stmt = $mysqli->prepare("SELECT url, occurrences FROM table WHERE keyword = ?");\n$stmt->bind_param("s", $_POST['keyword']);\n$stmt->execute();\n$stmt->store_result();\nif ($stmt->num_rows > 0) {\n    $stmt->bind_result($url, $occurrences);\n    while ($stmt->fetch()) {\n        $output .= "<keyword>" . htmlspecialchars($_POST['keyword']) . "</keyword><name>" . htmlspecialchars($url) . "</name><occur>" . htmlspecialchars($occurrences) . "</occur><queryTime>" . (substr($end_time - $start_time, 0, 5)) . "</queryTime>";\n    }\n} else {\n    $output .= "no";\n}