class Model_Table_Person implements SeekableIterator, Countable, ArrayAccess\n{\n    // ...\n\n    private function _getUser(Zend_Db_Table_Row $current)\n    {\n        $userType = $current->userType;\n        $userFactory = new UserFactory();\n        return $userFactory->createUser($userType, $current);\n    }\n}\n\nclass UserFactory\n{\n    public function createUser($userType, Zend_Db_Table_Row $row)\n    {\n        switch ($userType) {\n            case 'admin':\n                return new Model_Row_Administrator($row);\n            case 'associate':\n                return new Model_Row_Associate($row);\n            default:\n                throw new InvalidArgumentException("Invalid user type");\n        }\n    }\n}