include('inc/connection.php');\n$conn = dbConnect();\n\n// Define the variables and arrays\n$movieid = '123';\n$title = 'Example Movie';\n$year = 2022;\n$runtime = 120;\n$plot = 'This is an example movie plot.';\n\n$genres = array('Action', 'Crime', 'Drama');\n$actors = array('John Doe', 'Jane Doe', 'Bob Smith');\n$languages = array('English', 'Spanish', 'French');\n\n// Create SQL for movies table\n$sql_movies = 'INSERT INTO movies (movieid, title, year, runtime, plot)\n    VALUES(?, ?, ?, ?, ?)';\n\n// Create SQL for genres table\n$sql_genres = 'INSERT INTO genres (movieid, genre)\n    VALUES(?, ?)';\n\n// Create SQL for actors table\n$sql_actors = 'INSERT INTO actors (movieid, actor)\n    VALUES(?, ?)';\n\n// Create SQL for languages table\n$sql_languages = 'INSERT INTO languages (movieid, language)\n    VALUES(?, ?)';\n\n// Initialize prepared statements\n$stmt_movies = $conn->stmt_init();\n$stmt_genres = $conn->stmt_init();\n$stmt_actors = $conn->stmt_init();\n$stmt_languages = $conn->stmt_init();\n\n// Prepare and bind parameters for movies table\nif ($stmt_movies->prepare($sql_movies)) {\n    $stmt_movies->bind_param('isiis', $movieid, $title, $year, $runtime, $plot);\n    $stmt_movies->execute();\n}\n\n// Prepare and bind parameters for genres table\nif ($stmt_genres->prepare($sql_genres)) {\n    foreach ($genres as $genre) {\n        $stmt_genres->bind_param('is', $movieid, $genre);\n        $stmt_genres->execute();\n    }\n}\n\n// Prepare and bind parameters for actors table\nif ($stmt_actors->prepare($sql_actors)) {\n    foreach ($actors as $actor) {\n        $stmt_actors->bind_param('is', $movieid, $actor);\n        $stmt_actors->execute();\n    }\n}\n\n// Prepare and bind parameters for languages table\nif ($stmt_languages->prepare($sql_languages)) {\n    foreach ($languages as $language) {\n        $stmt_languages->bind_param('is', $movieid, $language);\n        $stmt_languages->execute();\n    }\n}