$array = array(\n    array('id' => 1, 'name' => 'Cat'),\n    array('id' => 2, 'name' => 'Mouse')\n);\n\n$array2 = array(\n    array('id' => 2, 'age' => 321),\n    array('id' => 1, 'age' => 123)\n);\n\n// Re-key the arrays by 'id' for easier merging\n$arrayById = array_column($array, null, 'id');\n$array2ById = array_column($array2, null, 'id');\n\n// Merge the arrays\n$merged = array_replace_recursive($arrayById, $array2ById);\n\n// Convert the merged array back to the original numeric index if needed\n$merged = array_values($merged);\n\nprint_r($merged);