<?php\n$files = glob('*.html'); // assuming your files are in the same directory\n\nforeach ($files as $file) {\n    $html = file_get_contents($file);\n    $dom = new DOMDocument();\n    $dom->loadHTML($html);\n    $xpath = new DOMXPath($dom);\n\n    $title = $xpath->query('//div[@id="abEntryTitleText"]')->item(0)->nodeValue;\n    $content = $xpath->query('//div[@class="abEntryParagraph"]')->item(0)->nodeValue;\n    $documentNumber = $xpath->query('//div[@class="abEntryDocumentNumber"]')->item(0)->nodeValue;\n\n    // Prepare the MySQL query\n    $query = "INSERT INTO questions (title, content, metadescription, lastupdated, related, visible, sortorder, userid, views, posvotes, negvotes, score, emailed, detectrelated, startdate, enableexpiry, expirydate, featured, workflowstatus)\n              VALUES (:title, :content, :metadescription, CURDATE(), 1, 1, 0, 1, 0, 1, 0, NULL, NULL, 1, CURDATE(), 0, NULL, 0, 'auto_approved')";\n\n    // Execute the query with prepared statements\n    $pdo = new PDO('mysql:host=your_host;dbname=your_database', 'your_username', 'your_password');\n    $stmt = $pdo->prepare($query);\n    $stmt->bindParam(':title', $title);\n    $stmt->bindParam(':content', $content);\n    $stmt->bindParam(':metadescription', $documentNumber);\n    $stmt->execute();\n}