// Retrieve latest videos from YouTube\n$youtubeVideos = array();\nforeach ($feed as $entry) {\n    $youtubeVideos[] = $entry->getVideoId();\n}\n\n// Retrieve existing videos from MySQL database\n$dbVideos = array();\n$rs->MoveFirst();\nwhile (!$rs->EOF) {\n    $dbVideos[] = $rs->fields['yt_id'];\n    $rs->MoveNext();\n}\n\n// Find new videos on YouTube that are not in the database\n$newVideos = array_diff($youtubeVideos, $dbVideos);\nforeach ($newVideos as $newVideoId) {\n    // Insert new video into database\n    $query = "INSERT INTO videos (yt_id) VALUES ('$newVideoId')";\n    mysql_query($query);\n}\n\n// Find videos in the database that are no longer on YouTube\n$deletedVideos = array_diff($dbVideos, $youtubeVideos);\nforeach ($deletedVideos as $deletedVideoId) {\n    // Delete video from database\n    $query = "DELETE FROM videos WHERE yt_id = '$deletedVideoId'";\n    mysql_query($query);\n}