function modifyArrayValues($array, $searchPattern, $insertString) {\n    foreach ($array as &$value) {\n        if (is_array($value)) {\n            $value = modifyArrayValues($value, $searchPattern, $insertString);\n        } elseif (preg_match("/$searchPattern/", $value)) {\n            $value = preg_replace("/($searchPattern)(\d+)/", "$1$insertString$2", $value);\n        }\n    }\n    return $array;\n}\n\n$array = [\n    "first" => ["baz1"],\n    "second" => ["foo1"],\n    "third" => ["foo2"]\n];\n\n$searchPattern = "foo";\n$insertString = "-bar";\n\n$modifiedArray = modifyArrayValues($array, $searchPattern, $insertString);\n\nprint_r($modifiedArray);