function is_serialized($data) {\n    if (!is_string($data)) {\n        return false;\n    }\n    $data = trim($data);\n    if ('N;' == $data) {\n        return true;\n    }\n    if (!preg_match('/^([adObis]):/', $data, $badions)) {\n        return false;\n    }\n    switch ($badions[1]) {\n        case 'a' :\n        case 'O' :\n        case 's' :\n            if (preg_match("/^{$badions[1]}:[0-9]+:.*[;}]\$/s", $data)) {\n                return true;\n            }\n            break;\n        case 'b' :\n        case 'i' :\n        case 'd' :\n            if (preg_match("/^{$badions[1]}:[0-9.E-]+;\$/", $data)) {\n                return true;\n            }\n            break;\n    }\n    return false;\n}