function filetimeToDateTime($filetime) {\n    // Reorder the bytes\n    $filetime = implode('', array_reverse(str_split($filetime, 2)));\n\n    // Convert to seconds\n    $seconds = hexdec($filetime) / 1e7;\n\n    // Add to the epoch (January 1, 1601)\n    $epoch = strtotime('1601-01-01 00:00:00 UTC');\n    $dateTime = date_create('@' . ($epoch + $seconds));\n\n    return $dateTime;\n}\n\n$filetime = '60 F3 47 D1 57 98 C9 01';\n$filetime = str_replace(' ', '', $filetime); // remove spaces\n\n$dateTime = filetimeToDateTime($filetime);\necho $dateTime->format('Y-m-d H:i:sP');