// autoloader.php\nclass Autoloader {\n    public static function load($class) {\n        if ($class === 'query') {\n            require_once 'wordpress/wp-includes/query.php';\n            rename_function('is_admin', 'wp_is_admin');\n        } elseif ($class === 'Security') {\n            require_once 'smf/Sources/Security.php';\n        }\n    }\n}\n\nspl_autoload_register(array('Autoloader', 'load'));