// Connect to the PostgreSQL database\n$conn = pg_connect("host=localhost dbname=mydatabase user=myuser password=mypassword");\n\n// Prepare the SQL query\n$query = "SELECT * FROM users WHERE id = $1";\n$stmt = pg_prepare($conn, "get_user", $query);\n\n// Execute the prepared statement with a parameter\n$id = 123;\n$result = pg_execute($conn, "get_user", array($id));\n\n// Fetch the result\n$row = pg_fetch_assoc($result);\n\n// Print the result\nprint_r($row);\n\n// Close the connection\npg_close($conn);