function array_path_exists($array1, $array2, $path = array()) {\n    if (empty($array1)) {\n        return false;\n    }\n\n    foreach ($array1 as $key => $value) {\n        $newPath = array_merge($path, array($key));\n        if (!isset($array2[$key])) {\n            return false;\n        }\n        if (is_array($value)) {\n            if (!array_path_exists($value, $array2[$key], $newPath)) {\n                return false;\n            }\n        }\n    }\n\n    return true;\n}\n\n$array1 = array("foo" => array("bar" => array("baz" => "value")));\n$array2 = array("foo" => array("bar" => array("baz" => "other value")));\n\nvar_dump(array_path_exists($array1, $array2)); // bool(true)