$bugzilla_url = 'https://your-bugzilla-instance.com/rest';\n$bug_id = 123;\n$comment = 'This is a new comment!';\n\n$curl = curl_init($bugzilla_url . '/bug/' . $bug_id . '/comment');\ncurl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($curl, CURLOPT_POST, true);\ncurl_setopt($curl, CURLOPT_POSTFIELDS, json_encode(array('comment' => $comment)));\n$headers = array('Content-Type: application/json');\ncurl_setopt($curl, CURLOPT_HTTPHEADER, $headers);\n\n$response = curl_exec($curl);\ncurl_close($curl);\n\nif ($response !== false) {\n    echo "Comment added successfully!";\n} else {\n    echo "Error adding comment: " . curl_error($curl);\n}