<?php\n$url = 'http://www.php.net/manual/en/';\n$dir = './php-docs';\n\nif (!file_exists($dir)) {\n    mkdir($dir, 0777, true);\n}\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$html = curl_exec($ch);\ncurl_close($ch);\n\n$dom = new DOMDocument();\n$dom->loadHTML($html);\n$links = $dom->getElementsByTagName('a');\n\nforeach ($links as $link) {\n    $href = $link->getAttribute('href');\n    if (preg_match('/^\/manual\/en\/(.*)$/', $href, $matches)) {\n        $file = $dir . '/' . $matches[1];\n        if (!file_exists($file)) {\n            $ch = curl_init($url . $matches[1]);\n            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n            $html = curl_exec($ch);\n            curl_close($ch);\n            file_put_contents($file, $html);\n        }\n    }\n}