class YourCompany_YourModule_Block_Navigation extends Mage_Catalog_Block_Navigation\n{\n    protected function _getCategoryCollection()\n    {\n        $collection = parent::_getCategoryCollection();\n        $storeId = Mage::app()->getStore()->getId();\n        $collection->getSelect()->join(\n            array('product' => 'catalog/product'),\n            'product.category_id = e.entity_id AND product.store_id = ' . $storeId,\n            array()\n        );\n        return $collection;\n    }\n}