$imagePath = 'path/to/image.jpg';\n$excelData = 'Your Excel data here...';\n\n// Create a new Excel file\n$excelFile = '';\n$excelFile .= pack('C*', 0x6d, 0x6f, 0x72, 0x65, 0x20, 0x68, 0x65, 0x72, 0x65); // "more here"\n$excelFile .= pack('v', 0x0100); // BIFF version\n$excelFile .= pack('v', 0x0005); // Excel version\n\n// Add image data\n$excelFile .= pack('C*', 0x19, 0x08); // Image record\n$excelFile .= pack('v', strlen($imagePath)); // Image path length\n$excelFile .= $imagePath; // Image path\n$excelFile .= pack('v', filesize($imagePath)); // Image size\n$excelFile .= file_get_contents($imagePath); // Image data\n\n// Add Excel data\n$excelFile .= $excelData;\n\n// Output Excel file\nheader('Content-Type: application/vnd.ms-excel');\nheader('Content-Disposition: attachment; filename="example.xls"');\necho $excelFile;\nexit;