// Example of how to update the Sessions_write function to handle errors\nfunction Sessions_write( $SessionID, $Data ) {\n    include_once( 'DBE.class.php' ) ;\n    $dbe = new DBE() ;\n\n    //default to true\n    $returnVal = TRUE ;\n\n    //update the session\n    $query = "UPDATE Sessions \n                        SET DataValue = '$Data'\n                    WHERE SessionID = '$SessionID'" ;\n\n    try {\n        $result = $dbe->Update( $query ) ; //pass the update stmt to the dbEngine..\n    } catch (Exception $e) {\n        // Handle the error\n        $returnVal = FALSE;\n    }\n\n    //return the return value\n    return( $returnVal ) ;\n}