// Assuming you have a MySQL connection established and a 'locations' table with a 'name' column\n\n// Query to retrieve location names\n$query = "SELECT name FROM locations";\n$result = mysqli_query($conn, $query);\n\n// Check if the query was successful\nif ($result) {\n    // Create a dropdown menu\n    echo '<select name="location">';\n    while ($row = mysqli_fetch_assoc($result)) {\n        $locationName = htmlspecialchars($row['name']);\n        $url = 'index.php?location=' . urlencode($locationName);\n        echo '<option value="' . $url . '">' . $locationName . '</option>';\n    }\n    echo '</select>';\n} else {\n    // Handle query error\n    echo 'Error: ' . mysqli_error($conn);\n}