$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Execute a query that generates a warning\n$query = "INSERT INTO jobs (JobName) VALUES ('Existing Job Name')";\n$mysqli->query($query);\n\n// Get warnings\n$warnings = $mysqli->get_warnings();\n\n// Iterate through warnings\nforeach ($warnings as $warning) {\n    echo "Warning: " . $warning->message . "\n";\n    // Handle the warning based on the message or code\n    if ($warning->code == 1062) {\n        // Handle duplicate entry warning\n        echo "Job name already exists!\n";\n    }\n}