<?php\n// Assuming you have an array of emails to send, with attachments\n$emails = array(\n    array('to' => 'recipient1@example.com', 'subject' => 'Test Email 1', 'body' => 'Hello, this is a test email.', 'attachments' => array('path/to/attachment1.pdf')),\n    array('to' => 'recipient2@example.com', 'subject' => 'Test Email 2', 'body' => 'Hello, this is another test email.', 'attachments' => array('path/to/attachment2.pdf')),\n);\n\n// Define the desired send time (6:00 PM)\n$sendTime = '18:00';\n\n// Get the current time\n$currentTime = date('H:i');\n\n// Check if it's time to send the emails\nif ($currentTime == $sendTime) {\n    // Send the emails\n    foreach ($emails as $email) {\n        // Create a new PHPMailer instance\n        $mail = new PHPMailer();\n\n        // Set the sender and recipient\n        $mail->SetFrom('your_email@example.com', 'Your Name');\n        $mail->AddAddress($email['to'], '');\n\n        // Set the subject and body\n        $mail->Subject = $email['subject'];\n        $mail->Body = $email['body'];\n\n        // Add attachments\n        foreach ($email['attachments'] as $attachment) {\n            $mail->AddAttachment($attachment);\n        }\n\n        // Send the email\n        $mail->Send();\n    }\n}\n?>