// Fetch the content from example.com\n$url = 'http://example.com';\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Parse the HTML content\n$doc = new DOMDocument();\n@$doc->loadHTML($response);\n$xpath = new DOMXPath($doc);\n\n// Get content within tags with class "x"\n$elements = $xpath->query("//*[@class='x']");\n\n// Pass content to specific variables\n$data = array();\nforeach ($elements as $element) {\n    $data[] = trim($element->nodeValue);\n}\n\n// Output the content in a pretty form (e.g., JSON)\nheader('Content-Type: application/json');\necho json_encode($data, JSON_PRETTY_PRINT);