require_once 'vendor/autoload.php';\n\n$client = new Google_Client();\n$client->setDeveloperKey('YOUR_API_KEY');\n$client->setApplicationName('YOUR_APPLICATION_NAME');\n\n$youtube = new Google_Service_YouTube($client);\n\n$videoId = 'VIDEO_ID_HERE';\n\n$commentThreads = $youtube->commentThreads->listCommentThreads('snippet', array(\n    'videoId' => $videoId,\n    'order' => 'time',\n    'maxResults' => 100\n));\n\n$comments = array();\nforeach ($commentThreads as $commentThread) {\n    $comments[] = $commentThread->snippet->topLevelComment->snippet->textOriginal;\n}\n\n// To fetch the next page of comments, use the nextPageToken\n$nextPageToken = $commentThreads->getNextPageToken();\nwhile ($nextPageToken) {\n    $commentThreads = $youtube->commentThreads->listCommentThreads('snippet', array(\n        'videoId' => $videoId,\n        'order' => 'time',\n        'maxResults' => 100,\n        'pageToken' => $nextPageToken\n    ));\n    foreach ($commentThreads as $commentThread) {\n        $comments[] = $commentThread->snippet->topLevelComment->snippet->textOriginal;\n    }\n    $nextPageToken = $commentThreads->getNextPageToken();\n}\n\nprint_r($comments);