if (isset($_POST['submit'])) {\n    $browser = $_SERVER['HTTP_USER_AGENT'];\n    $page = $_POST['page'];\n    $email = $_POST['email'];\n    $error = $_POST['error'];\n\n    $message = "<html><body>";\n    $message .= "Email: $email<br>";\n    $message .= "Page: $page<br>";\n    $message .= "OS/Browser: $browser<br>";\n    $message .= "Error: $error<br>";\n    $message .= "</body></html>";\n\n    $headers = array(\n        'MIME-Version: 1.0',\n        'Content-Type: text/html; charset=UTF-8',\n        'From: code@website.com',\n        'Reply-To: ' . $email,\n        'X-Priority: 1'\n    );\n\n    $subject = "[ERROR REPORT] Page: " . $page;\n\n    mail("myEmail@gmail.com", $subject, $message, implode("\r\n", $headers));\n\n    $mesg = "Thank you for your report!";\n}