class MixedArray implements ArrayAccess {\n    protected $_array = array();\n    protected $_numericKeys = array();\n\n    public function __construct($data) {\n        $this->_array = $data;\n        $this->_numericKeys = array_keys($data);\n    }\n\n    public function offsetExists($offset) {\n        return isset($this->_array[$offset]) || isset($this->_numericKeys[$offset]);\n    }\n\n    public function offsetGet($offset) {\n        if (is_numeric($offset)) {\n            return $this->_array[$this->_numericKeys[$offset]];\n        }\n        return $this->_array[$offset];\n    }\n\n    public function offsetSet($offset, $value) {\n        if (is_numeric($offset)) {\n            $key = $this->_numericKeys[$offset];\n            $this->_array[$key] = $value;\n        } else {\n            $this->_array[$offset] = $value;\n            $this->_numericKeys = array_keys($this->_array);\n        }\n    }\n\n    public function offsetUnset($offset) {\n        if (is_numeric($offset)) {\n            $key = $this->_numericKeys[$offset];\n            unset($this->_array[$key]);\n        } else {\n            unset($this->_array[$offset]);\n        }\n        $this->_numericKeys = array_keys($this->_array);\n    }\n}