function tui_cifp_ob_start() {\n    ob_start('tui_cifp_templatefilter');\n}\n\nfunction tui_cifp_templatefilter($content) {\n    global $categoryID;\n    $tag = '[categoryID]';\n    $replacement = tui_findPostThumbIMG($categoryID, 100, 100, 'categoryintro-thumbnail', '', '', '');\n    $content = str_replace($tag, $replacement, $content);\n    return $content;\n}\n\nadd_action('template_redirect', 'tui_cifp_ob_start');