$stmt = $pdo->prepare('SELECT * FROM mytable');\n$stmt->execute();\n\n// Set the cursor to be scrollable\n$stmt->setFetchMode(PDO::FETCH_ASSOC, PDO::FETCH_ORI_NEXT);\n\n// Fetch the first row\n$row = $stmt->fetch();\n\n// Fetch the next row\n$row = $stmt->fetch(PDO::FETCH_ORI_NEXT);\n\n// Fetch the previous row\n$row = $stmt->fetch(PDO::FETCH_ORI_PRIOR);\n\n// Fetch the last row\n$row = $stmt->fetch(PDO::FETCH_ORI_LAST);