$query = "SELECT \n            n.*,\n            (\n              SELECT \n                c.* \n              FROM \n                comments c \n              WHERE \n                c.news_id = n.id\n            ) AS comments\n          FROM \n            news n";\n\n$result = mysqli_query($conn, $query);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $comments = json_decode(json_encode($row['comments']), true);\n    $news[] = [\n        'id' => $row['id'],\n        // ... other news data ...\n        'comments' => $comments,\n    ];\n}\n\nprint_r($news);