$defaultImage = 'default.jpg'; // set default image\n$foundImage = false;\n\nforeach (glob("*.jpg") as $filename) {\n    $info = getimagesize($filename);\n    $aspectRatio = $info[1] / $info[0]; // calculate aspect ratio\n    if ($aspectRatio >= 0.9 && $aspectRatio <= 1.1) { // consider images with aspect ratio close to 1\n        echo "$filename size " . filesize($filename) . "<br>";\n        echo '<img src="'.$filename.'" height=150px><br>';\n        $foundImage = true;\n        break;\n    }\n}\n\nif (!$foundImage) {\n    echo '<img src="'.$defaultImage.'" height=150px><br>';\n}