$handle = fopen("yourfile.csv", "r");\n$buffer_size = 4096; // Read 4KB chunks\nif ($handle) {\n    $buffer = '';\n    while (!feof($handle)) {\n        $buffer .= fread($handle, $buffer_size);\n        // Find the last newline character in the buffer\n        $last_newline = strrpos($buffer, "\n");\n        if ($last_newline !== false) {\n            // Process all complete lines\n            $lines = explode("\n", substr($buffer, 0, $last_newline));\n            foreach ($lines as $line) {\n                // Process the line\n                echo $line . "\n";\n            }\n            // Keep the remaining part of the buffer\n            $buffer = substr($buffer, $last_newline + 1);\n        }\n    }\n    // Process any remaining content in $buffer\n    if (trim($buffer) !== '') {\n        echo $buffer;\n    }\n    fclose($handle);\n} else {\n    // Error opening the file\n}