<?php if($this->getCurrencyCount() > 1): ?>\n    <div class="currency-switcher">\n        <?php foreach ($this->getCurrencies() as $_code => $_name): ?>\n            <a href="<?php echo $this->getSwitchCurrencyUrl($_code) ?>" title="<?php echo Mage::helper('directory')->__('Change currency to') . ' ' . $_name ?>">\n                <?php echo $_code ?>\n            </a>\n        <?php endforeach; ?>\n    </div>\n<?php endif; ?>