class ArticleParser {\n    private $callback;\n\n    public function setCallback($callback) {\n        $this->callback = $callback;\n    }\n\n    public function parseArticle($article) {\n        // Parse the article...\n        $data = array(\n            'title' => $article->title,\n            'date' => $article->date,\n            'description' => $article->description,\n            'image' => $article->image\n        );\n\n        // Call the callback function with the parsed data\n        if ($this->callback) {\n            call_user_func($this->callback, $data);\n        }\n    }\n}\n\nclass ArticleProcessor {\n    public function processArticle($data) {\n        // Do something with the parsed data\n        echo "Title: {$data['title']}\n";\n        echo "Date: {$data['date']}\n";\n        echo "Description: {$data['description']}\n";\n        echo "Image: {$data['image']}\n";\n    }\n}\n\n$parser = new ArticleParser();\n$processor = new ArticleProcessor();\n\n// Set the callback function\n$parser->setCallback(array($processor, 'processArticle'));\n\n// Parse an article\n$parser->parseArticle($article);