$mysqli = new mysqli($host, $user, $password, $database);\n\n// Insert data into the first table\n$stmt = $mysqli->prepare("INSERT INTO table1 (data) VALUES (?)");\n$stmt->bind_param("s", $data);\n$stmt->execute();\n\n// Get the ID of the inserted row\n$id = $mysqli->insert_id;\n\n// Insert data into the second table using the ID\n$stmt = $mysqli->prepare("INSERT INTO table2 (id, additional_data) VALUES (?, ?)");\n$stmt->bind_param("is", $id, $additional_data);\n$stmt->execute();