$soapUrl = 'https://api.callfire.com/v1/voice/broadcast';\n$soapData = array('...' => '...'); // your SOAP data\n\n$ch = curl_init($soapUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($soapData));\ncurl_setopt($ch, CURLOPT_TIMEOUT, 0.1); // set timeout to 100ms\n\ncurl_exec($ch);\ncurl_close($ch);