try {\n    $slashdotRss = Zend_Feed::import('http://rss.slashdot.org/Slashdot/slashdot');\n} catch (Zend_Http_Client_Adapter_Exception $e) {\n    // feed import failed due to HTTP client adapter exception\n    echo "Exception caught importing feed: {$e->getMessage()}\n";\n    exit;\n} catch (Zend_Feed_Exception $e) {\n    // feed import failed due to feed exception\n    echo "Exception caught importing feed: {$e->getMessage()}\n";\n    exit;\n}