$autoloader = Zend_Loader_Autoloader::getInstance();\n$autoloader->suppressNotFoundWarnings(true);\n$autoloader->addCallback('autoloadOpenFlashChart', 'open_flash_chart');\n\nfunction autoloadOpenFlashChart($class) {\n    if (strpos($class, 'open_flash_chart') === 0) {\n        $file = dirname(__FILE__) . '/path/to/open-flash-chart/lib/' . str_replace('_', '/', $class) . '.php';\n        if (file_exists($file)) {\n            include $file;\n            return true;\n        }\n    }\n    return false;\n}