<?php\n$url = 'https://example.com/scuttle/submit'; // scuttle site submission URL\n$data = array(\n    'title' => 'Example Title',\n    'url' => 'https://example.com',\n    'description' => 'Example description',\n    'tags' => 'example,tag'\n);\n\n$ch = curl_init($url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookies.txt'); // if you need to handle cookies\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\nif ($response !== false) {\n    echo "Submission successful!";\n} else {\n    echo "Error submitting link: " . curl_error($ch);\n}