// crawler-output.php (on the crawler side)\n<?php\n$data = array(\n    'field1' => 'value1',\n    'field2' => 'value2',\n    // ...\n);\nheader('Content-Type: application/json');\necho json_encode($data);\n?>\n\n// api-receiver.php (on your site)\n<?php\n// Authentication and authorization checks\nif (!isset($_SERVER['HTTP_AUTHORIZATION']) || !authenticate($_SERVER['HTTP_AUTHORIZATION'])) {\n    http_response_code(401);\n    exit;\n}\n\n// Receive and validate data\n$jsonData = json_decode(file_get_contents('php://input'), true);\nif (!$jsonData || !validateData($jsonData)) {\n    http_response_code(400);\n    exit;\n}\n\n// Save data to database\n$db = new PDO('mysql:host=localhost;dbname=yourdb', 'username', 'password');\n$stmt = $db->prepare('INSERT INTO yourtable (field1, field2) VALUES (:field1, :field2)');\n$stmt->execute($jsonData);\n\n// Response\nhttp_response_code(201);\necho json_encode(array('message' => 'Data received successfully'));\n?>