// PHP example using Azure AD OAuth 2.0\n$clientId = 'your_client_id';\n$clientSecret = 'your_client_secret';\n$tenantId = 'your_tenant_id';\n$authorizationEndpoint = 'https://login.microsoftonline.com/' . $tenantId . '/oauth2/authorize';\n\n// Redirect user to Azure AD for authentication\nheader('Location: ' . $authorizationEndpoint . '?client_id=' . $clientId . '&response_type=code&redirect_uri=' . urlencode('http://your-php-app.com/callback'));\nexit;\n\n// Handle the redirect from Azure AD and validate the access token\nif (isset($_GET['code'])) {\n    // Obtain the access token\n    $tokenEndpoint = 'https://login.microsoftonline.com/' . $tenantId . '/oauth2/token';\n    $response = json_decode(file_get_contents($tokenEndpoint . '?grant_type=authorization_code&client_id=' . $clientId . '&client_secret=' . $clientSecret . '&code=' . $_GET['code'] . '&redirect_uri=' . urlencode('http://your-php-app.com/callback')), true);\n\n    // Validate the access token and extract the user's identity\n    $accessToken = $response['access_token'];\n    // ...\n}