$mysqli = new mysqli('localhost', 'username', 'password', 'database');\n\n// Enable profiling\n$mysqli->query('SET profiling = 1');\n\n// Execute your query\n$query = 'SELECT * FROM table_name';\n$mysqli->query($query);\n\n// Get the query ID\n$query_id = $mysqli->query('SELECT query_id FROM information_schema.profiling ORDER BY query_id DESC LIMIT 1')->fetch_assoc()['query_id'];\n\n// Get the profiling information\n$profile = $mysqli->query("SHOW PROFILE FOR QUERY $query_id");\n\n// Display the profiling information\nwhile ($row = $profile->fetch_assoc()) {\n    echo $row['Status'] . ': ' . $row['Duration'] . "\n";\n}\n\n// Disable profiling\n$mysqli->query('SET profiling = 0');