<?php\n// Set the password (change this to your desired password)\n$password = 'your_password_here';\n\n// Use a secure hash function to store the password\n$hashed_password = password_hash($password, PASSWORD_DEFAULT);\n\n// Function to check the password\nfunction check_password($input_password) {\n    global $hashed_password;\n    return password_verify($input_password, $hashed_password);\n}\n\n// Check if the password is provided and valid\nif (isset($_POST['password']) && check_password($_POST['password'])) {\n    // Password is valid, set a session variable to remember the user for a while\n    session_start();\n    $_SESSION['authenticated'] = true;\n    header('Location: protected_page.php');\n    exit;\n}\n\n// If the password is not provided or invalid, display the login form\n?>\n<form method="post">\n    <input type="password" name="password" placeholder="Enter password">\n    <input type="submit" value="Login">\n</form>