// Benchmarking Smarty vs plain PHP\n\n// Smarty template\nrequire_once('Smarty.class.php');\n$smarty = new Smarty();\n\n$template = '\n<html>\n  <body>\n    {section name=outer loop=$arr}\n      <p>{$arr[outer]}</p>\n    {/section}\n  </body>\n</html>\n';\n\n$smarty->assign('arr', range(1, 1000));\n$startTime = microtime(true);\nfor ($i = 0; $i < 100; $i++) {\n    $smarty->display('string:'.$template);\n}\n$endTime = microtime(true);\necho "Smarty took ".($endTime - $startTime)." seconds\n";\n\n// plain PHP template\n$template = '\n<html>\n  <body>\n    <?php foreach ($arr as $value) { ?>\n      <p><?php echo $value; ?></p>\n    <?php } ?>\n  </body>\n</html>\n';\n\n$arr = range(1, 1000);\n$startTime = microtime(true);\nfor ($i = 0; $i < 100; $i++) {\n    ob_start();\n    include('template.php');\n    ob_end_clean();\n}\n$endTime = microtime(true);\necho "Plain PHP took ".($endTime - $startTime)." seconds\n";