function mymodule_nodeapi(&$node, $op, $a3 = NULL, $a4 = NULL) {\n  switch ($op) {\n    case 'view':\n      if ($node->type == 'mybio') {\n        // Load the custom fields\n        $fields = content_fields('mybio');\n        \n        // Render the fields\n        foreach ($fields as $field_name => $field) {\n          $node->content[$field_name] = array(\n            '#value' => $field['value'],\n          );\n        }\n      }\n      break;\n  }\n}