// In app/controllers/errors.php\nclass AppError extends ErrorHandler {\n    function beforeFilter() {\n        // Set variables that are needed in the view\n        $this->set('myVariable', 'some value');\n    }\n    \n    function error404($params) {\n        // Custom 404 error handling\n        $this->set('title_for_layout', '404 - Not Found');\n        $this->set('message', 'The requested URL was not found on this server.');\n        $this->render('/errors/error404', $params);\n    }\n    \n    function error500($params) {\n        // Custom 500 error handling\n        $this->set('title_for_layout', '500 - Internal Server Error');\n        $this->set('message', 'An internal server error occurred.');\n        $this->render('/errors/error500', $params);\n    }\n}