<?php\n/**\n * @file\n * Module to reverse node order on the front page.\n */\n\n/**\n * Implementation of hook_db_rewrite_sql().\n */\nfunction custom_front_page_db_rewrite_sql($query, $primary_table, $primary_field, $args) {\n  if ($primary_table == 'n' && $primary_field == 'nid' && current_path() == '<front>') {\n    // Reverse the order of nodes on the front page.\n    $query = preg_replace('/ORDER BY n\.created DESC/', 'ORDER BY n.created ASC', $query);\n  }\n  return $query;\n}